mapper.select:
select
tsi.stack_id, tsi.spec_id, tsi.name, tsi.bp_id, tsi.user_id, tsi.create_user_id, tsi.status,  tsi.dst_node,
tsi.labels,  tsi.dev_need_info, tsi.auto_run, tsi.create_time, tsi.update_time, tsi.send_create_num,
tsi.start_num, tsi.fail_num, tsi.event_type, tsi.report_time, tsi.always_online
from tbl_cfgdata_stack_info tcsi left join tbl_stack_info tsi
on tcsi.stack_id = tsi.stack_id

  and tcsi.user_id = #{userId,jdbcType=VARCHAR}


  and tcsi.data_id = #{dataId,jdbcType=VARCHAR}


  and tcsi.data_group = #{group,jdbcType=VARCHAR}



    and tsi."status" not in

        #{item,jdbcType=INTEGER}
