mapper.select:
SELECT
t3.*
FROM
( SELECT * FROM tbl_helm_stack_info


    and release_name like concat('%',#{releaseName,jdbcType=VARCHAR},'%')


    and owner_id = #{releaseName,jdbcType=VARCHAR}


    and owner_bp_id = #{bpId,jdbcType=VARCHAR}


    and cluster_id in

       #{element,jdbcType=VARCHAR}




) t3
JOIN (
SELECT
t1.app_id
FROM
tbl_repo_app_category_bind_info t1
JOIN ( SELECT category_id FROM tbl_repo_category_info


    and category like concat('%',#{category,jdbcType=VARCHAR},'%')


) t2 ON t1.category_id = t2.category_id
) t5 ON t3.app_id = t5.app_id
order by t3.create_time desc

select count(1)
from tbl_helm_stack_info


    and owner_bp_id=#{ownerBpId,jdbcType=VARCHAR}


    and owner_id=#{ownerId,jdbcType=VARCHAR}


    and status != 5
