SELECT C.* FROM ( SELECT * FROM tbl_repo_app_info
and app_name like concat('%',#{appName,jdbcType=VARCHAR},'%')
and repo_name in
#{element,jdbcType=VARCHAR}
) C JOIN ( SELECT A .app_id FROM tbl_repo_app_category_bind_info A JOIN ( SELECT category_id FROM tbl_repo_category_info
and category like concat('%',#{category,jdbcType=VARCHAR},'%')
) b ON A.category_id = b.category_id ) d ON C.app_id = d.app_id order by c.create_time desc, c.app_id asc

