mapper.select:
select

from tbl_repo_app_version_info


    and app_id=#{appId,jdbcType=VARCHAR}


    and version=#{version,jdbcType=VARCHAR}


limit 1

select

from tbl_repo_app_version_info


        and repo_name=#{repoName,jdbcType=VARCHAR}


        and app_name=#{appName,jdbcType=VARCHAR}


        and version=#{version,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_repo_app_version_info


        and repo_name=#{repoName,jdbcType=VARCHAR}


        and app_name=#{appName,jdbcType=VARCHAR}


        and version=#{version,jdbcType=VARCHAR}
