mapper.select:
select

  distinct


from tbl_cmp_baremetal_instance




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_baremetal_instance
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and instance_id = #{instanceId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_baremetal_instance
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and instance_id = #{instanceId,jdbcType=VARCHAR}

mapper.select:
select count(distinct instance_id) from tbl_cmp_baremetal_instance,tbl_cmp_baremetal_device
where tbl_cmp_baremetal_instance.device_id = tbl_cmp_baremetal_device.device_id and tbl_cmp_baremetal_instance.phase_status != -1

  and tbl_cmp_baremetal_device.ee_user=#{userId}


  and tbl_cmp_baremetal_instance.phase_status=#{baremetalInstancePhaseStatus}

and tbl_cmp_baremetal_device.cloud_id=#{cloudId}
