mapper.select:
select

  distinct


from tbl_cmp_eip_map




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_eip_map
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and eip_map_id = #{eipMapId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_eip_map
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and eip_map_id = #{eipMapId,jdbcType=VARCHAR}
