mapper.select:
select

  distinct


from tbl_cmp_eip




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_eip
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and eip_id = #{eipId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_eip
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and eip_id = #{eipId,jdbcType=VARCHAR}

mapper.select:
select global_port from tbl_cmp_eip LEFT JOIN tbl_cmp_eip_map on tbl_cmp_eip.eip_id = tbl_cmp_eip_map.eip_id
LEFT JOIN tbl_cmp_port_map on tbl_cmp_eip_map.eip_map_id = tbl_cmp_port_map.eip_map_id


    and tbl_cmp_eip_map.ee_user = #{userId}

  and tbl_cmp_eip_map.cloud_id = #{cloudId}
  and tbl_cmp_eip_map.ee_status != -1
  and tbl_cmp_port_map.protocol = #{protocol}
  and tbl_cmp_eip.eip_id = #{eipId}
  and tbl_cmp_port_map.cloud_id = #{cloudId}
  and tbl_cmp_port_map.ee_status != -1
