  distinct


from tbl_cmp_instance_group




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_instance_group
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and instance_group_id = #{instanceGroupId,jdbcType=VARCHAR}

