mapper.select:
select 

from tbl_cmp_os_ml2_port_bindings
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and port_id = #{portId,jdbcType=VARCHAR}
  and host = #{host,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_os_ml2_port_bindings
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and port_id = #{portId,jdbcType=VARCHAR}
  and host = #{host,jdbcType=VARCHAR}

mapper.update:
update tbl_cmp_os_ml2_port_bindings
set vif_type = #{vifType,jdbcType=VARCHAR},
  vnic_type = #{vnicType,jdbcType=VARCHAR},
  profile = #{profile,jdbcType=VARCHAR},
  vif_details = #{vifDetails,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR},
  ee_bp = #{eeBp,jdbcType=VARCHAR},
  ee_user = #{eeUser,jdbcType=VARCHAR},
  ee_status = #{eeStatus,jdbcType=INTEGER}
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and port_id = #{portId,jdbcType=VARCHAR}
  and host = #{host,jdbcType=VARCHAR}
