mapper.select:
select 

from tbl_cmp_os_networks
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_os_networks
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.select:
select

  distinct


from tbl_cmp_os_networks




  and ((is_default is not null) or (shared = 1) or (

    project_id = #{projectId,jdbcType=VARCHAR}


  and


    ee_user = #{userId,jdbcType=VARCHAR}

  ))


  order by ${example.orderByClause}
