mapper.select:
select 

from tbl_cmp_os_ports
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_os_ports
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.select:
select id from tbl_cmp_os_ports
where cloud_id = #{cloudId} and ee_status != -1

  and device_id = #{deviceId}

select

  distinct


from tbl_cmp_os_ports t1
left join
(select id as networkid from tbl_cmp_os_networks
where cloud_id = #{cloudId}  and ee_status != -1

  and ((is_default is not null) or (shared = 1) or (

    project_id = #{projectId,jdbcType=VARCHAR}


    and


    ee_user = #{userId,jdbcType=VARCHAR}

  ))
)
t2 on t1.network_id = t2.networkid




  order by ${example.orderByClause}
