mapper.select:
select 

from tbl_cmp_os_routers
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_os_routers
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.select:
select

  distinct

 , firewall_id
from tbl_cmp_os_routers left join
(select router_id, firewall_id from tbl_cmp_es_firewall_bindings where cloud_id = #{cloudId} and ee_status != -1

  and firewall_id = #{firewallId,jdbcType=VARCHAR}
)
t on tbl_cmp_os_routers.id = t.router_id




  and

    firewall_id is null


    firewall_id = #{firewallId,jdbcType=VARCHAR}



  order by ${example.orderByClause}
