mapper.select:
select

  distinct


from tbl_cmp_pci_device_group




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_pci_device_group
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and device_group_id = #{deviceGroupId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_pci_device_group
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and device_group_id = #{deviceGroupId,jdbcType=VARCHAR}
