mapper.select:
select tbl_cmp_pci_device.device_id,
tbl_cmp_pci_device.type as pci_device_type, tbl_cmp_pci_device.name as pci_device_name, tbl_cmp_pci_device.phase_status,
tbl_cmp_pci_device.create_time, tbl_cmp_pci_device.update_time from tbl_cmp_pci_device




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select tbl_cmp_hypervisor_node.node_id, tbl_cmp_hypervisor_node.name as node_name, tbl_cmp_pci_device.name as pci_device_name, tbl_cmp_pci_device.type as pci_device_type, tbl_cmp_pci_device.user_id, tbl_cmp_pci_device.phase_status, tbl_cmp_pci_device.device_id, tbl_cmp_pci_device.create_time, tbl_cmp_pci_device.update_time, tbl_cmp_vm_instance.vm_instance_id, tbl_cmp_vm_instance.name as vm_instance_name from tbl_cmp_hypervisor_node LEFT JOIN tbl_cmp_pci_device on tbl_cmp_hypervisor_node.node_id = tbl_cmp_pci_device.node_id LEFT JOIN tbl_cmp_vm_instance on tbl_cmp_pci_device.vm_instance_id = tbl_cmp_vm_instance.vm_instance_id
order by ${orderByClause}
limit #{pageSize} offset #{startRow}

SELECT cloud_id, cloudName, gpuName, COALESCE(gpuTotal,0) as gpuTotal, COALESCE(availableGpuCount,0) as availableGpuCount FROM (SELECT t1.cloud_id, tbl_cloud_info.name AS cloudName, t1.gpuName, t1.gpuTotal, t1.availableGpuCount FROM (SELECT cloud_id, tbl_cmp_pci_device.name AS gpuName, COUNT ( tbl_cmp_pci_device.device_id ) AS gpuTotal, SUM (CASE WHEN tbl_cmp_pci_device.vm_instance_id IS NULL THEN 1 ELSE 0 END) AS availableGpuCount FROM tbl_cmp_pci_device WHERE ee_status != -1
AND cloud_id IN
#{item}
GROUP BY (cloud_id, name)) t1 LEFT JOIN tbl_cloud_info ON t1.cloud_id = tbl_cloud_info.cloud_id
WHERE tbl_cloud_info.provider_id = #{providerId,jdbcType=VARCHAR}
) t2
WHERE gpuName like concat('%',#{gpuName,jdbcType=VARCHAR},'%')
