mapper.select:
select 

from tbl_cmp_port_map
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and port_map_id = #{portMapId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_port_map
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and port_map_id = #{portMapId,jdbcType=VARCHAR}

mapper.insert:
