  protocol, global_port, local_port, 
  status, create_time, update_time, 
  ee_bp, ee_user, ee_status
  )
values (#{cloudId,jdbcType=VARCHAR}, #{portMapId,jdbcType=VARCHAR}, #{eipMapId,jdbcType=VARCHAR}, 
  #{protocol,jdbcType=SMALLINT}, #{globalPort,jdbcType=INTEGER}, #{localPort,jdbcType=INTEGER}, 
  #{status,jdbcType=SMALLINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{eeBp,jdbcType=VARCHAR}, #{eeUser,jdbcType=VARCHAR}, #{eeStatus,jdbcType=INTEGER}
  )
