mapper.select:
select

  distinct


from tbl_cmp_resource_stats




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_resource_stats
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and stats_id = #{statsId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_resource_stats
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and stats_id = #{statsId,jdbcType=VARCHAR}
