mapper.select:
select 

from tbl_cmp_security_group
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and sg_id = #{sgId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_security_group
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and sg_id = #{sgId,jdbcType=VARCHAR}

mapper.sql:
sg.ee_status != -1 and RULE.ee_status != -1

  and sg.cloud_id = #{cloudId} and RULE.cloud_id = #{cloudId}


  and sg.ee_user=#{userId}


  and sg.sg_id=#{sgId}


  and sg.name like '%' || #{name} || '%'

sg.ee_status != -1 and compute_ins.ee_status != -1

  and sg.cloud_id = #{cloudId} and compute_ins.cloud_id = #{cloudId}


  and sg.ee_user=#{userId} and compute_ins.ee_user=#{userId}


  and sg.sg_id=#{sgId}


  and sg.name like '%' || #{name} || '%'

mapper.select:
select count(distinct sg.sg_id) from tbl_cmp_security_group as sg


    and sg.cloud_id = #{cloudId}

  and sg.ee_status != -1

    and sg.ee_user = #{userId}


    and sg.sg_id = #{sgId}


    and sg.name like '%' || #{name} || '%'



      and sg.phase_status = #{phaseStatus}


      and sg.phase_status != #{phaseStatus}

    and sg.phase_status is null

select

from tbl_cmp_security_group
where cloud_id = #{cloudId,jdbcType=VARCHAR}
and ee_id = #{eeId,jdbcType=VARCHAR}
