mapper.select:
select

  distinct


from tbl_cmp_vm_instance




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_vm_instance
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and vm_instance_id = #{vmInstanceId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_vm_instance
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and vm_instance_id = #{vmInstanceId,jdbcType=VARCHAR}

mapper.select:
select COALESCE(sum(tbl_cmp_flavor.root_disk),0) from tbl_cmp_flavor, tbl_cmp_vm_instance where tbl_cmp_vm_instance.flavor_id = tbl_cmp_flavor.flavor_id
and tbl_cmp_vm_instance.phase_status != -1

  and tbl_cmp_vm_instance.user_id=#{userId}


  and tbl_cmp_vm_instance.cloud_id=#{cloudId}

and tbl_cmp_vm_instance.ee_status != -1

select COALESCE(sum(tbl_cmp_disk_info."size"),0) from tbl_cmp_disk_info, tbl_cmp_vm_instance where tbl_cmp_vm_instance.vm_instance_id = tbl_cmp_disk_info.vm_instance_id
and tbl_cmp_vm_instance.phase_status != -1

  and tbl_cmp_vm_instance.user_id=#{userId}


  and tbl_cmp_vm_instance.cloud_id=#{cloudId}

and tbl_cmp_vm_instance.ee_status != -1

SELECT phase_status, COUNT(*) as count
FROM tbl_cmp_vm_instance
where ee_status != -1 and cloud_id = #{cloudId,jdbcType=VARCHAR}

  and ee_user = #{userId,jdbcType=VARCHAR}


  and ee_bp = #{bpId,jdbcType=VARCHAR}

GROUP BY phase_status

select

from tbl_cmp_vm_instance
where cloud_id = #{cloudId,jdbcType=VARCHAR}
and ee_id = #{eeId,jdbcType=VARCHAR}
