select tbl_cmp_volume.volume_id, tbl_cmp_volume.user_id, tbl_cmp_volume.create_time, tbl_cmp_volume.update_time, tbl_cmp_volume.storage_pool_id, tbl_cmp_volume.name, tbl_cmp_volume.size, tbl_cmp_volume.type, tbl_cmp_vm_instance.vm_instance_id as vmInstanceId, tbl_cmp_vm_instance.name as vmName, tbl_cmp_volume.phase_status, tbl_cmp_volume.ee_bp, tbl_cmp_volume.ee_user, tbl_cmp_volume.ee_status, tbl_cmp_volume.charge_type, tbl_cmp_volume.price_unit, tbl_cmp_volume.period, tbl_cmp_volume.expire_time, tbl_cmp_volume.source from tbl_cmp_volume left join tbl_cmp_vm_instance on tbl_cmp_volume.vm_id = tbl_cmp_vm_instance.vm_instance_id
order by ${orderByClause}
limit #{pageSize} offset #{startRow}

select tbl_cmp_volume.volume_id, tbl_cmp_volume.user_id, tbl_cmp_volume.create_time, tbl_cmp_volume.update_time, tbl_cmp_volume.storage_pool_id, tbl_cmp_volume.name, tbl_cmp_volume.size, tbl_cmp_volume.phase_status, tbl_cmp_image.image_id, tbl_cmp_image.image_name, tbl_cmp_image.image_os_type, tbl_cmp_image.image_os_vendor, tbl_cmp_volume.type, tbl_cmp_volume.ee_bp, tbl_cmp_volume.ee_user, tbl_cmp_volume.charge_type, tbl_cmp_volume.price_unit, tbl_cmp_volume.period, tbl_cmp_volume.expire_time from tbl_cmp_volume left join (select * from tbl_cmp_image where cloud_id = #{cloudId} and ee_status != -1) tbl_cmp_image on tbl_cmp_volume.image_id = tbl_cmp_image.image_id
