mapper.select:
select

  distinct


from tbl_cmp_volume_snap




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select 

from tbl_cmp_volume_snap
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and volume_snap_id = #{volumeSnapId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_cmp_volume_snap
where cloud_id = #{cloudId,jdbcType=VARCHAR}
  and volume_snap_id = #{volumeSnapId,jdbcType=VARCHAR}

mapper.select:
select tbl_cmp_volume_snap.volume_snap_id, tbl_cmp_volume_snap.volume_id, tbl_cmp_volume_snap.name, tbl_cmp_volume_snap.is_current, tbl_cmp_volume_snap.update_time, tbl_cmp_volume_snap.create_time, tbl_cmp_volume.storage_pool_id, tbl_cmp_volume_snap.phase_status, tbl_cmp_volume_snap.description, tbl_cmp_volume.name as volume_name, tbl_cmp_volume_snap.user_id, tbl_cmp_volume_snap.parent_id, tbl_cmp_volume_snap.ee_bp, tbl_cmp_volume_snap.ee_user from tbl_cmp_volume_snap left join tbl_cmp_volume on tbl_cmp_volume_snap.volume_id = tbl_cmp_volume.volume_id
order by ${orderByClause}
limit #{pageSize} offset #{startRow}
