mapper.select:
select

from tbl_iam_attachment


    and project_id=#{projectId,jdbcType=VARCHAR}


    and (project_id is null or  project_id = '')


    and principal_id=#{principalId,jdbcType=VARCHAR}


    and principal_type=#{principalType,jdbcType=INTEGER}


    and target_id=#{targetId,jdbcType=VARCHAR}


    and target_type=#{targetType,jdbcType=INTEGER}


limit 1

mapper.delete:
delete from tbl_iam_attachment


    and id in

      #{item,jdbcType=VARCHAR}

mapper.select:
 select
     t1.id as id, t1.project_id as projectId, t1.project_name as projectName, t1.projectDisplayName as projectDisplayName,
     t1.principal_id as principalId, t1.principal_type as principalType, t1.principal_name AS principalName,t1.target_id as policyId,
     t1.description as description, t1.attach_time as attachTime, t1.bp_id as bpId, t1.user_id as userId,
     t2.name as policyName, t2.policy_type as policyType, t2.display_name as policyDisplayName
 from (
 select t3.*, t4.display_name as projectDisplayName from
 (

     SELECT
     ti1.*,
     ti2.user_name AS principal_name
     FROM
    (select * from tbl_iam_attachment


         and bp_id=#{bpId,jdbcType=VARCHAR}


         and user_id=#{userId,jdbcType=VARCHAR}


         and project_id=#{projectId,jdbcType=VARCHAR}


         and principal_id=#{principalId,jdbcType=VARCHAR}


         and principal_type=1


         and target_id=#{targetId,jdbcType=VARCHAR}


         and target_type=1

     ) ti1
     LEFT JOIN tbl_user_info ti2 ON ti1.principal_id = ti2.user_id
     AND ti1.principal_type = 1


     SELECT
     ti1.*,
     ti2.user_name AS principal_name
     FROM
     (select * from tbl_iam_attachment


         and bp_id=#{bpId,jdbcType=VARCHAR}


         and user_id=#{userId,jdbcType=VARCHAR}


         and project_id=#{projectId,jdbcType=VARCHAR}


         and principal_id=#{principalId,jdbcType=VARCHAR}


         and principal_type=1


         and target_id=#{targetId,jdbcType=VARCHAR}


         and target_type=1

     ) ti1
     LEFT JOIN tbl_user_info ti2 ON ti1.principal_id = ti2.user_id
     AND ti1.principal_type = 1


     SELECT
     ti1.*,
     ti3.ROLE AS principal_name
     FROM
     (select * from tbl_iam_attachment


         and bp_id=#{bpId,jdbcType=VARCHAR}


         and user_id=#{userId,jdbcType=VARCHAR}


         and project_id=#{projectId,jdbcType=VARCHAR}


         and principal_id=#{principalId,jdbcType=VARCHAR}


         and principal_type=3


         and target_id=#{targetId,jdbcType=VARCHAR}


         and target_type=1


     ) ti1
     LEFT JOIN tbl_iam_role ti3 ON ti1.principal_id = CAST ( ti3.role_id AS VARCHAR )
     AND ti1.principal_type = 3


     SELECT
     ti1.*,
     ti2.user_name AS principal_name
     FROM
     (select * from tbl_iam_attachment


         and bp_id=#{bpId,jdbcType=VARCHAR}


         and user_id=#{userId,jdbcType=VARCHAR}


         and project_id=#{projectId,jdbcType=VARCHAR}


         and principal_id=#{principalId,jdbcType=VARCHAR}


         and principal_type=1


         and target_id=#{targetId,jdbcType=VARCHAR}


         and target_type=1

     ) ti1
     LEFT JOIN tbl_user_info ti2 ON ti1.principal_id = ti2.user_id
     AND ti1.principal_type = 1
     UNION ALL
     SELECT
     ti1.*,
     ti3.ROLE AS principal_name
     FROM
     (select * from tbl_iam_attachment


         and bp_id=#{bpId,jdbcType=VARCHAR}


         and user_id=#{userId,jdbcType=VARCHAR}


         and project_id=#{projectId,jdbcType=VARCHAR}


         and principal_id=#{principalId,jdbcType=VARCHAR}


         and principal_type=3


         and target_id=#{targetId,jdbcType=VARCHAR}


         and target_type=1


     ) ti1
     LEFT JOIN tbl_iam_role ti3 ON ti1.principal_id = CAST ( ti3.role_id AS VARCHAR )
     AND ti1.principal_type = 3

)t3 left join
 tbl_Iam_project t4 on t3.project_id = t4.id
 ) t1 join (
 select * from tbl_iam_policy


     and display_name like concat('%',#{policyName,jdbcType=VARCHAR},'%')


     and policy_type=#{policyType,jdbcType=INTEGER}


 ) t2 on t1.target_id = t2.id

select
t1.id as id, t1.project_id as projectId, t1.project_name as projectName, t1.projectDisplayName as projectDisplayName,
t1.principal_id as principalId, t1.principal_type as principalType, t1.target_id as roleId,
t1.description as description, t1.attach_time as attachTime, t1.bp_id as bpId, t1.user_id as userId,
t2.platform as rolePlatform, t2.role as roleName, t2.role_type as roleType
from (
select t3.*, t4.display_name as projectDisplayName from
(select * from
tbl_iam_attachment


    and bp_id=#{bpId,jdbcType=VARCHAR}


    and user_id=#{userId,jdbcType=VARCHAR}


    and project_id=#{projectId,jdbcType=VARCHAR}


    and principal_id=#{principalId,jdbcType=VARCHAR}


    and principal_type=#{principalType,jdbcType=INTEGER}


    and target_id=#{targetId,jdbcType=VARCHAR}


    and target_type=3

)t3 left join
tbl_Iam_project t4 on t3.project_id = t4.id
) t1 join (
select cast(role_id as VARCHAR) as role_Id, platform,role, project_id, role_type from tbl_iam_role


    and role like concat('%',#{roleName,jdbcType=VARCHAR},'%')


    and role_type=#{roleType,jdbcType=INTEGER}


) t2 on t1.target_id = t2.role_id

select

from tbl_iam_attachment


    and target_type=#{targetType,jdbcType=INTEGER}


    and target_id=#{targetId,jdbcType=VARCHAR}


    and bp_id=#{bpId,jdbcType=VARCHAR}

select

from tbl_iam_attachment


    and project_id=#{projectId,jdbcType=VARCHAR}


    and principal_id=#{principalId,jdbcType=VARCHAR}


    and principal_type=#{principalType,jdbcType=INTEGER}


    and target_type=#{targetType,jdbcType=INTEGER}

mapper.delete:
delete from tbl_iam_attachment


        and project_id=#{projectId,jdbcType=VARCHAR}


        and principal_id=#{principalId,jdbcType=VARCHAR}


        and principal_type=#{principalType,jdbcType=INTEGER}
