mapper.insert:

insert into tbl_iam_policy_version (policy_id, version_id, document, 
  create_time, update_time, description
  )
values (#{policyId,jdbcType=VARCHAR}, #{versionId,jdbcType=VARCHAR}, #{document,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{description,jdbcType=VARCHAR}
  )

mapper.select:
select

from tbl_iam_policy_version


    and policy_id=#{policyId,jdbcType=VARCHAR}


    and version_id=#{versionId,jdbcType=VARCHAR}

mapper.delete:
delete from tbl_iam_policy_version


    and policy_id=#{policyId,jdbcType=VARCHAR}


    and version_id=#{versionId,jdbcType=VARCHAR}

mapper.select:
select count(1)
from
(select policy_id from
tbl_iam_policy_version


    and policy_id=#{policyId,jdbcType=VARCHAR}


    and version_id=#{versionId,jdbcType=VARCHAR}

) t1
join
(select id from
tbl_iam_policy


    and id=#{policyId,jdbcType=VARCHAR}

) t2
on t1.policy_id = t2.id

select
    document
from tbl_iam_policy_version


    and policy_id=#{policyId,jdbcType=VARCHAR}


    and version_id=#{versionId,jdbcType=VARCHAR}


limit 1

select version_id from tbl_iam_policy_version
and policy_id=#{policyId,jdbcType=VARCHAR}
order by CAST(substring(version_id FROM '[0-9]+$') AS INTEGER) desc limit 1

select count(1)
from tbl_iam_policy_version


    and policy_id=#{policyId,jdbcType=VARCHAR}
