mapper.select:
select

from tbl_iam_project


        and "name" like concat('%',#{name,jdbcType=VARCHAR},'%')


        and bp_id=#{bpId,jdbcType=VARCHAR}


      and "status" = 1


    and "type"=#{type,jdbcType=INTEGER}

select count(1)
from tbl_iam_project


    and "name"=#{name,jdbcType=VARCHAR}


    and bp_id=#{bpId,jdbcType=VARCHAR}


    and "status" = 1

select count(1)
from tbl_iam_project


    and id=#{id,jdbcType=VARCHAR}


    and bp_id=#{bpId,jdbcType=VARCHAR}


    and "status" = 1

select

from tbl_iam_project


    and id=#{id,jdbcType=VARCHAR}


    and bp_id=#{bpId,jdbcType=VARCHAR}


    and "status" = 1


limit 1

choose.when:
select "name" from tbl_iam_project
and id=#{id,jdbcType=VARCHAR}
limit 1

select display_name from tbl_iam_project
and id=#{id,jdbcType=VARCHAR}
limit 1

mapper.select:
select "name" from tbl_iam_project
and "name"=#{name,jdbcType=VARCHAR}
order by name desc limit 1

SELECT
t1.*
FROM
( SELECT * FROM tbl_iam_project


        and "name" like concat('%',#{name,jdbcType=VARCHAR},'%')


        and "type"=#{type,jdbcType=INTEGER}


) t1
JOIN ( SELECT bp_id, kind FROM tbl_user_info


    and user_id=#{userId,jdbcType=VARCHAR}


) t2 on t1.bp_id = t2.bp_id

  or (t2.kind in (0, 1) and t1.name = #{defaultAdminProjectName,jdbcType=VARCHAR})

limit 1
