mapper.insert:

insert into tbl_iam_service (id, "name", display_name, 
  iam_code, parent_id, lrn_format, 
  lrn_regex, "status", "enable", 
  description, create_time, update_time
  )
values (#{id,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{displayName,jdbcType=VARCHAR}, 
  #{iamCode,jdbcType=VARCHAR}, #{parentId,jdbcType=VARCHAR}, #{lrnFormat,jdbcType=VARCHAR}, 
  #{lrnRegex,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, #{enable,jdbcType=INTEGER}, 
  #{description,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select count(1) from tbl_iam_service
and "name"=#{name,jdbcType=VARCHAR}
and "status" = 1

select count(1) from tbl_iam_service
and id=#{id,jdbcType=VARCHAR}
and "status" = 1

select "name" from tbl_iam_service
and id=#{id,jdbcType=VARCHAR}
limit 1

select display_name from tbl_iam_service
and id=#{id,jdbcType=VARCHAR}
limit 1

select id from tbl_iam_service
and "name"=#{name,jdbcType=VARCHAR}
limit 1

select iam_code from tbl_iam_service
and id=#{id,jdbcType=VARCHAR}
limit 1

select count(1)
from tbl_iam_service


        and iam_code=#{iamCode,jdbcType=VARCHAR}
