mapper.select:
select

  distinct


from tbl_user_info




  order by ${orderByClause}


  limit #{pageSize} offset #{startRow}

select user_name from tbl_user_info
and user_id=#{userId,jdbcType=VARCHAR}
limit 1

SELECT bp_name FROM tbl_bp_info WHERE bp_id = ( select bp_id from tbl_user_info
and user_id=#{userId,jdbcType=VARCHAR}
LIMIT 1 )

select t1.*, t2.bp_name as bpName from ( select user_id as userId, user_name as userName, email, phone, bp_id as bpId, create_time as createTime, update_time as updateTime from tbl_user_info
and user_id in
#{item,jdbcType=VARCHAR}
) t1 join tbl_bp_info t2 on t1.bpId = t2.bp_id

select count(1) from tbl_user_info
and bp_id=#{bpId,jdbcType=VARCHAR}
and status != -1

SELECT
t2.bp_id as bpId,
t2.user_id as userId,
t2.user_name as userName,
t2.bp_name as bpName,
t2.email as email,
t2.phone as phone,
t2.create_time as registrationTime
FROM
 (
select t3.*, t4.bp_name from (
SELECT * FROM tbl_user_info


    and kind = 2


    and bp_id in

      #{bpId}



    and user_id in

      #{userId}



    and user_id = #{userId,jdbcType=VARCHAR}


    and user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')


    and create_time > #{minCreateTime,jdbcType=TIMESTAMP}


    and create_time < #{maxCreateTime,jdbcType=TIMESTAMP}


) t3 join (select * from tbl_bp_info) t4 on t3.user_name = t4.master_user and t3.bp_id = t4.bp_id
) t2
ORDER BY registrationTime ${sortDirection}
