mapper.select:
select

from tbl_ims_registry


    and registry_id in

      #{registryId}



    and registry_name like concat('%',#{likeRegistryName,jdbcType=VARCHAR},'%')


    and user_id=#{userId,jdbcType=VARCHAR}


    and status in

      #{status,jdbcType=INTEGER}



    and status <> -1


order by update_time desc, create_time desc

SELECT t2.registry_id, t2.url, t2.admin_name, t2.admin_passwd FROM ( SELECT registry_id FROM tbl_ims_registry_region
and region_id =#{regionId,jdbcType=VARCHAR}
) t1 JOIN (select * from tbl_ims_registry where status = 6) t2 ON t1.registry_id = t2.registry_id
