mapper.select:
    select

    from tbl_omc_receiver


        and bp_id=#{bpId,jdbcType=VARCHAR}


        and user_id=#{userId,jdbcType=VARCHAR}


            and "name"=#{name,jdbcType=VARCHAR}


order by create_time desc

SELECT
t1.*,
t5.notify_channels
FROM
( SELECT * FROM tbl_omc_receiver


    and id=#{id,jdbcType=VARCHAR}


) t1
JOIN (
SELECT
t4.receiver_id,
t4.notify_channels
FROM
( SELECT * FROM tbl_omc_notify_rule


    and alarm_id=#{alarmId,jdbcType=VARCHAR}


) t3
JOIN ( SELECT * FROM tbl_omc_notify_object


    and receiver_id=#{id,jdbcType=VARCHAR}


) t4 ON t3.ID = t4.notify_rule_id
) t5 ON t1.ID = t5.receiver_id
limit 1
